await robot.userRequest('参观引导');
const isPresent = await robot.detectHuman();
if(isPresent){
    await robot.speak('您好，欢迎来到我们的公司！');
    await robot.goto('作品展示区');
    await robot.speak('这里是我们的作品展示区。眼前是一个双臂机器人，其手臂可以上下移动，并且可以360度旋转身体。同时，我们公司还在开发虚实结合的激情人测试平台，可以满足不同家具场景下的模拟测试。');
    await robot.speak('介绍完毕，请跟我返回接待区。');
    await robot.goto('接待区');
    await robot.speak('欢迎再次参观！');
}else{
    await robot.speak('接待区没有人，请稍后再来参观。');
}

