await robot.userRequest('参观公司');
await robot.goto('作品展示区');
const isHumanDetected = await robot.detectHuman();

if(isHumanDetected){
    const visitOption = await robot.ask('请问你想先了解炫酷的双臂机器人还是同样炫酷的虚实结合机器人测试平台？');

    if(visitOption=="双臂机器人"){
        await robot.speak('欢迎参观作品展示区，这里展示了我们炫酷的双臂机器人。');
        await robot.speak('双臂机器人具有高度灵活的双臂操作能力，可以完成各种复杂任务。');

    }else if(visitOption=="虚实结合"){
        await robot.speak('欢迎参观作品展示区，这里展示了我们的虚实结合机器人测试平台。');
        await robot.speak('虚实结合机器人测试平台是一个集成了虚拟现实和实际机器人操作的创作环境。');

    }else{
        await robot.speak('抱歉，我没有理解你的选择。');
    }

    await robot.speak('请继续跟我走');

}else{
    await robot.speak('作品展示区没有人参观');
}

await robot.goto('创作室');
