await robot.userRequest('参观介绍');

await robot.speak('欢迎参观我们的实验室！');
await robot.speak('我是一台智能机器人，具有丰富的功能和技术特点。');
await robot.speak('我可以进行语音交互、人脸识别、语音导览等任务。');
await robot.speak('我还可以在不同的场景中应用，如家庭、医疗、教育等领域。');

const hasPerson = await robot.detectHuman();
if(hasPerson){
    await robot.speak('前方有人，请注意安全！');
}else{
    await robot.speak('前方没有人，可以继续导览。');
}

await robot.speak('现在，让我们来介绍一下公司的主要展品。');
await robot.speak('在作品展示区，我们展示了一台双臂机器人。');
await robot.speak('它的手臂可以上下移动，并且可以三百六十度旋转身体。');

await robot.goto('会客厅');
await robot.speak('在会客厅，我们正在开发一款虚实结合的机器人测试平台。');
await robot.speak('它可以模拟不同家居场景，进行机器人的测试和验证。');

await robot.speak('感谢您的参观，希望您对我们的机器人有更深入的了解！');

